/*-------------------------------------------------------

     Description of displayListOfFiles.c:
     displays on screen the LMF files in current dir 

-------------------------------------------------------*/
#include <stdio.h>
#include <stdlib.h>

void displayListOfFiles()
{
  printf("List of LMF files in your current directory :\n");
  printf
      ("test.cch and test.ccs have been generated by \n our GATE simulation\n\n");
  printf("-----------------------------------------\n");
  system("ls *.cch *.ccs");	/* displays on screen the LMF files in current dir */
  printf("-----------------------------------------\n");

  printf("\n");
}
